import { useEffect, useState } from "react";

export default function useWindowWidth() {
  const { width, setWidth } = useState(1536);
  useEffect(() => {
    window.addEventListener("resize", (e) => {
      setWidth(() => {
        return e.target.innerWidth;
      });
      return width;
    });
  }, []);
  return width;
}
