export default function Layout() {
  return (
    <div className="container-main bg-[var(--white)] rounded-xl  h-[50vh] min-w-[600px]">
      <div className="content-image w-1/2 h-full">
        <img
          className="h-full w-full rounded-l-xl"
          src={require("../images/image-product-desktop.jpg")}
        ></img>
      </div>
      <div className="content-description">
        <h4>PERFUME</h4>
        <h3>Gabrielle Essence Eau De Parfum</h3>
        <p>
          A floral, solar and voluptupus inerpretation composed by Olivier
          Polge, Perfumer-Creator for the house of CHANEL.
        </p>
      </div>
    </div>
  );
}
